import './styles.css';
import React from 'react';

function App() {
  return (
    <React.Fragment>
      <HeroSection />
      <FeaturesSection />
    </React.Fragment>
  );
}

function HeroSection() {
  return (
    <section id="hero">
    <div class="hero-content">
      <h2>Start learning about finance today</h2>
      <button onclick="showSection('features')">Get Started now</button>
    </div>
    </section>
  );
}

function FeaturesSection() {
  var features = {
    "learning-modules": ["Learning Modules", "Choose from a variety of specific learning modules.", ""]
  }
  return (
    <section id="features">
      <AddFeature
        head="Learning Modules"
        content="Choose from a variety of specific learning modules."
        buttonContent="" />
      <AddFeature
        head="Learning Modules"
        content="Choose from a variety of specific learning modules."
        buttonContent="Explore Modules" />
    </section>
  );
}

function AddFeature({head, content, showSectionVar, buttonContent}) {
  const handleClick = () => {
    const section = document.querySelector(`${showSectionVar}`);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div class='feature'>
      <h3>{head}</h3>
      <p>{content}</p>
      <button onclick={handleClick}>{buttonContent}</button>
    </div>
  );
}


export default App;
